import React, { useState } from 'react';

const fieldLabels = [
    'rundet_day',
    'rundet_anpow',
    'rundet_type',
    'rundet_pcrex_group',
    'rundet_qpedset',
    'rundet_deadtimetau',
    'rundet_comment',
    'experiment'
];

function SQLDBQuery() {
    const [runNumber, setRunNumber] = useState('');
    const [fields, setFields] = useState(Array(8).fill(''));

    // Only allow integer input for run number
    const handleRunNumberChange = (e) => {
        const value = e.target.value;
        if (value === '' || /^[0-9]+$/.test(value)) {
            setRunNumber(value);
        }
    };

    return (
        <div className="sql-db-query">
            <label>
                Moller Run Number:&nbsp;
                <input
                    type="text"
                    value={runNumber}
                    onChange={handleRunNumberChange}
                    placeholder="Enter run number"
                    inputMode="numeric"
                    pattern="[0-9]*"
                />
            </label>
            <button onClick={() => { /* fetch logic here */ }}>Fetch</button>
            <div style={{ display: 'flex', marginTop: '1em', gap: '1em' }}>
                {fieldLabels.map((label, idx) => (
                    <div key={label} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                        <label style={{ fontSize: '0.9em' }}>{label}</label>
                        <input
                            type="text"
                            value={fields[idx]}
                            readOnly
                            style={{ width: 120 }}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default SQLDBQuery;
